// src/components/Admin/EditQuiz.js

import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import '../../styles/editQuiz.css';

const EditQuiz = ({ quizzes, setQuizzes }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [quiz, setQuiz] = useState(null);
  const [questionText, setQuestionText] = useState('');
  const [options, setOptions] = useState({ option1: '', option2: '', option3: '', option4: '' });
  const [correctAnswer, setCorrectAnswer] = useState('');

  useEffect(() => {
    const foundQuiz = quizzes.find(q => q.id === parseInt(id));
    if (foundQuiz) {
      setQuiz(foundQuiz);
      // Assuming we edit the first question for simplicity
      const firstQuestion = foundQuiz.questions[0];
      setQuestionText(firstQuestion.text);
      setOptions({
        option1: firstQuestion.options[0].text || '',
        option2: firstQuestion.options[1].text || '',
        option3: firstQuestion.options[2].text || '',
        option4: firstQuestion.options[3].text || ''
      });
      setCorrectAnswer(firstQuestion.correctAnswer);
    }
  }, [id, quizzes]);

  const handleUpdate = () => {
    if (quiz) {
      const updatedQuestions = quiz.questions.map((q, index) => 
        index === 0 ? {
          text: questionText,
          options: [
            { text: options.option1 },
            { text: options.option2 },
            { text: options.option3 },
            { text: options.option4 }
          ],
          correctAnswer: correctAnswer
        } : q
      );
      const updatedQuiz = { ...quiz, questions: updatedQuestions };
      const updatedQuizzes = quizzes.map(q => q.id === quiz.id ? updatedQuiz : q);
      setQuizzes(updatedQuizzes);
      navigate('/admin/quiz-management'); // Navigate back to Quiz Management
    }
  };

  const handleCancel = () => {
    navigate('/admin/quiz-management'); // Navigate back to Quiz Management
  };

  if (!quiz) return <div>Loading...</div>;

  return (
    <div className="edit-quiz">
      <h3>Edit Quiz</h3>
      <form>
        <div>
          <label>Question Text:</label>
          <input
            type="text"
            value={questionText}
            onChange={(e) => setQuestionText(e.target.value)}
          />
        </div>
        <div>
          <label>Option 1:</label>
          <input
            type="text"
            value={options.option1}
            onChange={(e) => setOptions({ ...options, option1: e.target.value })}
          />
        </div>
        <div>
          <label>Option 2:</label>
          <input
            type="text"
            value={options.option2}
            onChange={(e) => setOptions({ ...options, option2: e.target.value })}
          />
        </div>
        <div>
          <label>Option 3:</label>
          <input
            type="text"
            value={options.option3}
            onChange={(e) => setOptions({ ...options, option3: e.target.value })}
          />
        </div>
        <div>
          <label>Option 4:</label>
          <input
            type="text"
            value={options.option4}
            onChange={(e) => setOptions({ ...options, option4: e.target.value })}
          />
        </div>
        <div>
          <label>Correct Answer:</label>
          <input
            type="text"
            value={correctAnswer}
            onChange={(e) => setCorrectAnswer(e.target.value)}
          />
        </div>
        <button type="button" onClick={handleUpdate}>Update</button>
        <button type="button" onClick={handleCancel}>Cancel</button>
      </form>
    </div>
  );
};

export default EditQuiz;
