import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/editProfile.css'; // Adjust path as per your file structure
import { useNavigate } from 'react-router-dom';
import Footer from './Footer';
import { toast } from 'react-toastify';
import { FaEye, FaEyeSlash } from 'react-icons/fa'; // Ensure correct import path for icons

const UPDATE_API = 'http://localhost:5164/updateUser'; // Adjust API endpoint

const EditProfile = () => {
  const [user, setUser] = useState({
    id: '',
    username: '',
    name: '',
    email: '',
    address: '',
    picture: null // Assuming picture is stored as a file object
  });

  const [editing, setEditing] = useState(false); // State to toggle editing mode
  const [fileInput, setFileInput] = useState(null); // State to manage file input
  const [loading, setLoading] = useState(false);
  const [previewImage, setPreviewImage] = useState(null); // State for previewing image
  const [showPassword, setShowPassword] = useState(false); // State to toggle password visibility
  const navigate = useNavigate();
  const [isLogoutPopupVisible, setIsLogoutPopupVisible] = useState(false);

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    setLoading(true);
    try {
      const currentUser = JSON.parse(localStorage.getItem('currentUser'));
      const userEmail = currentUser?.email;

      const response = await axios.post('http://localhost:5164/viewUsers', {
        eventID: "1001",
        addInfo: { email: userEmail }
      });

      const userData = response.data.rData?.items[0] || {}; // Assuming only one user data is returned
      setUser(userData);

      if (userData.picture) {
        setPreviewImage(userData.picture);
      } else {
        setPreviewImage(null);
      }

    } catch (error) {
      console.error('Error fetching user data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewImage(reader.result);
        setFileInput(file);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleEdit = () => {
    setEditing(true);
  };

  const handleSave = async () => {
    try {
      const formData = new FormData();
      formData.append('eventID', "1001");
      formData.append('id', user.id);
      formData.append('username', user.username);
      formData.append('name', user.name);
      formData.append('email', user.email);
      formData.append('address', user.address);

      if (fileInput) {
        formData.append('picture', fileInput);
      }

      const response = await axios.post(UPDATE_API, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.status === 200) {
        toast.success('Profile updated successfully');
        setEditing(false);
        fetchUserData(); // Refresh user data after update
      } else {
        toast.error('Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      toast.error('An error occurred while updating the profile');
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleLogout = () => {
    sessionStorage.clear();
    navigate('/');
  };

  const showLogoutPopup = () => {
    setIsLogoutPopupVisible(true);
  };

  const hideLogoutPopup = () => {
    setIsLogoutPopupVisible(false);
  };

  const confirmLogout = () => {
    hideLogoutPopup();
    handleLogout();
  };

  return (
    <>
      <div className="edit-profile">
        <h2>Edit Profile</h2>
        <div className="profile-picture">
          <img src={previewImage || 'defaultprofile.jpg'} alt="Profile" />
          <input type="file" accept="image/*" onChange={handleFileChange} />
        </div>
        <div className="profile-details">
          <label>Username:</label>
          <input type="text" name="username" value={user.username} readOnly={!editing} onChange={handleChange} />
          <label>Name:</label>
          <input type="text" name="name" value={user.name} readOnly={!editing} onChange={handleChange} />
          <label>Email:</label>
          <input type="email" name="email" value={user.email} readOnly={!editing} onChange={handleChange} />
          <label>Address:</label>
          <textarea name="address" value={user.address} readOnly={!editing} onChange={handleChange} />
        </div>
        <div className="profile-actions">
          {!editing ? (
            <button onClick={handleEdit}>Edit</button>
          ) : (
            <>
              <button onClick={handleSave}>Save</button>
              <button onClick={() => setEditing(false)}>Cancel</button>
            </>
          )}
        </div>
      </div>

      {isLogoutPopupVisible && (
        <div className="logout-popup">
          <div className="logout-popup-content">
            <p>Are you sure you want to logout?</p>
            <button onClick={confirmLogout}>Yes</button>
            <button onClick={hideLogoutPopup}>No</button>
          </div>
        </div>
      )}
      <Footer />
    </>
  );
};

export default EditProfile;
