import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../../styles/editQuiz.css';

const EditQuiz = ({ quizzes, setQuizzes }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isEditing = !!id;
  const [quizData, setQuizData] = useState({ title: '', description: '', questions: [] });
  const [questionData, setQuestionData] = useState({ text: '', options: '' });

  useEffect(() => {
    if (isEditing) {
      const quiz = quizzes.find(q => q.id === parseInt(id));
      if (quiz) {
        setQuizData(quiz);
      }
    }
  }, [id, quizzes, isEditing]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setQuizData({ ...quizData, [name]: value });
  };

  const handleQuestionChange = (e) => {
    const { name, value } = e.target;
    setQuestionData({ ...questionData, [name]: value });
  };

  const handleAddQuestion = (e) => {
    e.preventDefault();
    const newQuestion = {
      text: questionData.text,
      options: questionData.options.split(',').map(option => ({ text: option.trim() })),
    };
    setQuizData({ ...quizData, questions: [...quizData.questions, newQuestion] });
    setQuestionData({ text: '', options: '' });
  };

  const handleSaveChanges = (e) => {
    e.preventDefault();
    if (isEditing) {
      const updatedQuizzes = quizzes.map(quiz =>
        quiz.id === quizData.id ? { ...quiz, ...quizData } : quiz
      );
      setQuizzes(updatedQuizzes);
    } else {
      const newQuiz = {
        id: quizzes.length + 1,
        ...quizData,
      };
      setQuizzes([...quizzes, newQuiz]);
    }
    navigate('/quiz-management');
  };

  return (
    <div className="form-container">
      <h3>{isEditing ? 'Edit Quiz' : 'Create Quiz'}</h3>
      <form onSubmit={handleSaveChanges}>
        <div>
          <label>Title:</label>
          <input
            type="text"
            name="title"
            value={quizData.title}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <input
            type="text"
            name="description"
            value={quizData.description}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">{isEditing ? 'Save Changes' : 'Create Quiz'}</button>
      </form>

      <div className="form-container">
        <h3>Add Question</h3>
        <form onSubmit={handleAddQuestion}>
          <div>
            <label>Question Text:</label>
            <input
              type="text"
              name="text"
              value={questionData.text}
              onChange={handleQuestionChange}
            />
          </div>
          <div>
            <label>Options (comma separated):</label>
            <input
              type="text"
              name="options"
              value={questionData.options}
              onChange={handleQuestionChange}
            />
          </div>
          <button type="submit">Add Question</button>
        </form>
      </div>
    </div>
  );
};

export default EditQuiz;
