import React, { useState, useEffect } from 'react';
import '../../styles/quizManagement.css';
import axios from 'axios';

const QuizManagement = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [quizData, setQuizData] = useState({ title: '', description: '', questions: [] });
  const [questionData, setQuestionData] = useState({
    text: '',
    options: ['', '', '', ''],
    correctAnswer: ''
  });

  useEffect(() => {
    const fetchQuizzes = async () => {
      try {
        // Replace with the actual endpoint URL for fetching quizzes
        const response = await axios.post('http://localhost:5164/viewCardQuiz', { eventID: "1001" });

        if (response.status === 200) {
          const responseData = response.data;
          if (responseData.rData && responseData.rData.items) {
            setQuizzes(responseData.rData.items);
            console.log("Quizzes:", responseData.rData.items);
          } else {
            console.log("No quizzes data in response");
          }
        }
      } catch (error) {
        setError("Error fetching quizzes: " + error.message);
        console.error("Error fetching quizzes:", error);
      }
    };

    fetchQuizzes();
  }, []);

  const handleEditQuiz = (quiz) => {
    setSelectedQuiz(quiz);
    setQuizData({ title: quiz.title, description: quiz.description, questions: quiz.questions });
    setIsEditing(true);
  };

  const handleDeleteQuiz = (quizId) => {
    setQuizzes(quizzes.filter(quiz => quiz.id !== quizId));
  };

  const handleSaveChanges = (e) => {
    e.preventDefault();
    const updatedQuizzes = quizzes.map(quiz =>
      quiz.id === selectedQuiz.id ? { ...quiz, ...quizData } : quiz
    );
    setQuizzes(updatedQuizzes);
    setIsEditing(false);
    setSelectedQuiz(null);
    setQuizData({ title: '', description: '', questions: [] });
  };

  const handleCreateQuiz = (e) => {
    e.preventDefault();
    const newQuiz = {
      id: quizzes.length + 1,
      title: quizData.title,
      description: quizData.description,
      questions: quizData.questions,
    };
    setQuizzes([...quizzes, newQuiz]);
    setQuizData({ title: '', description: '', questions: [] });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setQuizData({ ...quizData, [name]: value });
  };

  const handleQuestionChange = (e, index) => {
    const { name, value } = e.target;
    const newOptions = [...questionData.options];
    if (name.startsWith('option')) {
      newOptions[parseInt(name.slice(-1)) - 1] = value;
    } else {
      setQuestionData({ ...questionData, [name]: value });
    }
    setQuestionData({ ...questionData, options: newOptions });
  };

  const handleAddQuestion = (e) => {
    e.preventDefault();
    const newQuestion = {
      text: questionData.text,
      options: questionData.options,
      correctAnswer: questionData.correctAnswer
    };
    setQuizData({ ...quizData, questions: [...quizData.questions, newQuestion] });
    setQuestionData({ text: '', options: ['', '', '', ''], correctAnswer: '' });
  };

  const handleEditQuestionChange = (index, field, value) => {
    const updatedQuestions = [...quizData.questions];
    updatedQuestions[index] = { ...updatedQuestions[index], [field]: value };
    setQuizData({ ...quizData, questions: updatedQuestions });
  };

  const handleEditQuestion = (questionIndex) => {
    setIsEditing(true);
    setQuestionData({
      text: quizData.questions[questionIndex].text,
      options: quizData.questions[questionIndex].options,
      correctAnswer: quizData.questions[questionIndex].correctAnswer
    });
  };

  const handleDeleteQuestion = (questionIndex) => {
    const updatedQuestions = quizData.questions.filter((_, i) => i !== questionIndex);
    setQuizData({ ...quizData, questions: updatedQuestions });
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
    setSelectedQuiz(null);
    setQuizData({ title: '', description: '', questions: [] });
  };

  return (
    <div>
      <h2>Quiz Management</h2>
      {quizzes.map(quiz => (
        <div key={quiz.id}>
          <h3>{quiz.title}</h3>
          <p>{quiz.description}</p>
          <button onClick={() => handleEditQuiz(quiz)}>Edit</button>
          <button onClick={() => {
            if (window.confirm('Are you sure you want to delete this quiz?')) {
              handleDeleteQuiz(quiz.id);
            }
          }}>Delete</button>
        </div>
      ))}

      {isEditing && (
        <div>
          <h3>Edit Quiz</h3>
          <form onSubmit={handleSaveChanges}>
            <div>
              <label>Title:</label>
              <input
                type="text"
                name="title"
                value={quizData.title}
                onChange={handleChange}
                required
              />
            </div>
            <div>
              <label>Description:</label>
              <input
                type="text"
                name="description"
                value={quizData.description}
                onChange={handleChange}
                required
              />
            </div>

            {quizData.questions.map((question, index) => (
              <div key={index}>
                <h4>Question {index + 1}</h4>
                <div>
                  <label>Question Text:</label>
                  <input
                    type="text"
                    value={question.text}
                    onChange={(e) => handleEditQuestionChange(index, 'text', e.target.value)}
                    required
                  />
                </div>
                <div>
                  <label>Option 1:</label>
                  <input
                    type="text"
                    value={question.options[0]}
                    onChange={(e) => handleEditQuestionChange(index, 'options', e.target.value, 0)}
                    required
                  />
                </div>
                <div>
                  <label>Option 2:</label>
                  <input
                    type="text"
                    value={question.options[1]}
                    onChange={(e) => handleEditQuestionChange(index, 'options', e.target.value, 1)}
                    required
                  />
                </div>
                <div>
                  <label>Option 3:</label>
                  <input
                    type="text"
                    value={question.options[2]}
                    onChange={(e) => handleEditQuestionChange(index, 'options', e.target.value, 2)}
                    required
                  />
                </div>
                <div>
                  <label>Option 4:</label>
                  <input
                    type="text"
                    value={question.options[3]}
                    onChange={(e) => handleEditQuestionChange(index, 'options', e.target.value, 3)}
                    required
                  />
                </div>
                <div>
                  <label>Correct Answer:</label>
                  <input
                    type="text"
                    value={question.correctAnswer}
                    onChange={(e) => handleEditQuestionChange(index, 'correctAnswer', e.target.value)}
                    required
                  />
                </div>
                <button type="button" onClick={() => handleDeleteQuestion(index)}>Delete Question</button>
              </div>
            ))}

            <button type="submit">Update Quiz</button>
            <button type="button" onClick={handleCancelEdit}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default QuizManagement;
