// src/components/Dashboard/ContactUs.js

import React, { useState } from 'react';
import axios from "axios";
import { useNavigate } from 'react-router-dom';

const ContactUs = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  const payload = {
    eventID: "1002",
    addInfo: {
      name: formData.name,
      email: formData.email,
      message: formData.message
    }
  };

  try {
    console.log('Sending request with payload:', payload);
    const response = await axios.post('http://localhost:5164/insertContactUs', payload);
    console.log('Response from server:', response.data); // Log entire response for detailed inspection

    // Check if response has expected properties
    if (response.data && response.data.rData && response.data.rData.rMessage) {
      const res = response.data.rData.rMessage;
      console.log('API response message:', res); // Log specific response message

      if (res === "Successful") {
        alert("Form submitted successfully!");
        setFormData({
          name: '',
          email: '',
          message: ''
        });
      } else {
        alert(res);
      }
    } else {
      // Log the entire response for debugging
      console.error('Unexpected response format:', response);
      alert('Unexpected response format. Please try again later.');
    }
  } catch (error) {
    console.error('Error submitting form:', error);
    alert('Error submitting form. Please try again later.'); // Handle error
  }
};

  return (
    <div>
      <h2>Contact Us</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Message:</label>
          <textarea
            name="message"
            value={formData.message}
            onChange={handleChange}
            rows={5}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ContactUs;
