// src/components/Admin/QuizManagement.js

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import QuizItem from '../Quiz/QuizItem';
import '../../styles/quizManagement.css';

const QuizManagement = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [quizData, setQuizData] = useState({ title: '', description: '', questions: [] });
  const [questionData, setQuestionData] = useState({ text: '', options: '' });

  useEffect(() => {
    // Mock data fetching function (replace with actual API call)
    const fetchQuizzes = async () => {
      const mockQuizzes = [
        {
          id: 1,
          title: 'Quiz 1',
          description: 'Description of Quiz 1',
          questions: [
            { text: 'What is 2 + 2?', options: [{ text: '3' }, { text: '4' }, { text: '5' }], correctAnswer: '4' }
          ],
        },
        // Add more quizzes if needed
      ];
      setQuizzes(mockQuizzes);
    };

    fetchQuizzes();
  }, []);

  const handleEditQuiz = (quiz) => {
    setSelectedQuiz(quiz);
    setQuizData({ title: quiz.title, description: quiz.description, questions: quiz.questions });
    setIsEditing(true);
  };

  const handleDeleteQuiz = (quizId) => {
    if (window.confirm('Are you sure you want to delete this quiz?')) {
      setQuizzes(quizzes.filter(quiz => quiz.id !== quizId));
    }
  };

  const handleSaveChanges = (e) => {
    e.preventDefault();
    const updatedQuizzes = quizzes.map(quiz =>
      quiz.id === selectedQuiz.id ? { ...quiz, ...quizData } : quiz
    );
    setQuizzes(updatedQuizzes);
    setIsEditing(false);
    setSelectedQuiz(null);
    setQuizData({ title: '', description: '', questions: [] });
  };

  const handleCreateQuiz = (e) => {
    e.preventDefault();
    const newQuiz = {
      id: quizzes.length + 1,
      title: quizData.title,
      description: quizData.description,
      questions: quizData.questions,
    };
    setQuizzes([...quizzes, newQuiz]);
    setQuizData({ title: '', description: '', questions: [] });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setQuizData({ ...quizData, [name]: value });
  };

  const handleQuestionChange = (e) => {
    const { name, value } = e.target;
    setQuestionData({ ...questionData, [name]: value });
  };

  const handleAddQuestion = (e) => {
    e.preventDefault();
    const newQuestion = {
      text: questionData.text,
      options: [
        { text: questionData.option1 },
        { text: questionData.option2 },
        { text: questionData.option3 },
        { text: questionData.option4 }
      ],
      correctAnswer: questionData.correctAnswer
    };
    setQuizData({ ...quizData, questions: [...quizData.questions, newQuestion] });
    setQuestionData({ text: '', option1: '', option2: '', option3: '', option4: '', correctAnswer: '' });
  };

  return (
    <div>
      <h2>Quiz Management</h2>
      {quizzes.map(quiz => (
        <div key={quiz.id} className="quiz-item">
          <h3>{quiz.title}</h3>
          <p>{quiz.description}</p>
          <button onClick={() => handleEditQuiz(quiz)}>
            <Link to={`/admin/edit-quiz/${quiz.id}`}>Edit</Link>
          </button>
          <button onClick={() => handleDeleteQuiz(quiz.id)}>Delete</button>
        </div>
      ))}

      <div className="form-container">
        <h3>{isEditing ? 'Edit Quiz' : 'Create Quiz'}</h3>
        <form onSubmit={isEditing ? handleSaveChanges : handleCreateQuiz}>
          <div>
            <label>Title:</label>
            <input
              type="text"
              name="title"
              value={quizData.title}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Description:</label>
            <input
              type="text"
              name="description"
              value={quizData.description}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit">{isEditing ? 'Save Changes' : 'Create Quiz'}</button>
          {isEditing && <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>}
        </form>

        {isEditing && (
          <div className="question-form">
            <h4>Add/Edit Question</h4>
            <form onSubmit={handleAddQuestion}>
              <div>
                <label>Question Text:</label>
                <input
                  type="text"
                  name="text"
                  value={questionData.text}
                  onChange={handleQuestionChange}
                  required
                />
              </div>
              <div>
                <label>Option 1:</label>
                <input
                  type="text"
                  name="option1"
                  value={questionData.option1}
                  onChange={handleQuestionChange}
                  required
                />
              </div>
              <div>
                <label>Option 2:</label>
                <input
                  type="text"
                  name="option2"
                  value={questionData.option2}
                  onChange={handleQuestionChange}
                  required
                />
              </div>
              <div>
                <label>Option 3:</label>
                <input
                  type="text"
                  name="option3"
                  value={questionData.option3}
                  onChange={handleQuestionChange}
                />
              </div>
              <div>
                <label>Option 4:</label>
                <input
                  type="text"
                  name="option4"
                  value={questionData.option4}
                  onChange={handleQuestionChange}
                />
              </div>
              <div>
                <label>Correct Answer:</label>
                <input
                  type="text"
                  name="correctAnswer"
                  value={questionData.correctAnswer}
                  onChange={handleQuestionChange}
                  required
                />
              </div>
              <button type="submit">Add Question</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default QuizManagement;
