import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../styles/editProfile.css';

const EditProfile = () => {
  const [user, setUser] = useState({
    username: '',
    name: '',
    email: '',
    address: '',
    picture: null // assuming picture is stored as a file object
  });

  const [editing, setEditing] = useState(false); // State to toggle editing mode
  const [fileInput, setFileInput] = useState(null); // State to manage file input

  // Fetch user data from backend on component mount
  useEffect(() => {
    fetchUserData();
  }, []);

  // Fetch user data function
  const fetchUserData = async () => {
    try {
      const response = await axios.post('http://localhost:5164/viewUsers'); // Replace with your backend API endpoint
      const userData = response.data; // Assuming response is an object containing user data
      setUser(userData);
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  // Handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  // Handle file upload
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFileInput(file);
  };

  // Handle edit button click
  const handleEdit = () => {
    setEditing(true);
  };

  // Handle update button click
  const handleUpdate = async () => {
    try {
      // Prepare data to send to backend
      const formData = new FormData();
      formData.append('username', user.username);
      formData.append('name', user.name);
      formData.append('email', user.email);
      formData.append('address', user.address);
      if (fileInput) {
        formData.append('picture', fileInput);
      }

      // Make API call to update user data
      const response = await axios.put('YOUR_UPDATE_API_URL', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log('Update Response:', response.data);
      // Optionally handle success message or update UI
      setEditing(false); // Exit editing mode
    } catch (error) {
      console.error('Error updating user data:', error);
    }
  };

  // Render component
  return (
    <div className="edit-profile">
      <h2>Edit Profile</h2>
      <div className="profile-picture">
        <img src={user.picture ? URL.createObjectURL(user.picture) : '/default-avatar.png'} alt="Profile" />
        <input type="file" accept="image/*" onChange={handleFileChange} />
      </div>
      <div className="profile-details">
        <label>Username:</label>
        <input type="text" name="username" value={user.username} readOnly={!editing} onChange={handleChange} />
        <label>Name:</label>
        <input type="text" name="name" value={user.name} readOnly={!editing} onChange={handleChange} />
        <label>Email:</label>
        <input type="email" name="email" value={user.email} readOnly={!editing} onChange={handleChange} />
        <label>Address:</label>
        <textarea name="address" value={user.address} readOnly={!editing} onChange={handleChange} />
      </div>
      <div className="profile-actions">
        {!editing ? (
          <button onClick={handleEdit}>Edit</button>
        ) : (
          <>
            <button onClick={handleUpdate}>Update</button>
            <button onClick={() => setEditing(false)}>Cancel</button>
          </>
        )}
      </div>
    </div>
  );
};

export default EditProfile;
