import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import QuizItem from '../Quiz/QuizItem';
import '../../styles/quizManagement.css';

const QuizManagement = () => {
  const [quizzes, setQuizzes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Mock data fetching function (replace with actual API call)
    const fetchQuizzes = async () => {
      const mockQuizzes = [
        {
          id: 1,
          title: 'Quiz 1',
          description: 'Description of Quiz 1',
          questions: [
            {
              text: 'What is 2 + 2?',
              options: [
                { text: '3' },
                { text: '4' },
                { text: '5' },
              ],
            },
          ],
        },
      ];
      setQuizzes(mockQuizzes);
    };

    fetchQuizzes();
  }, []);

  const handleEditQuiz = (quiz) => {
    navigate(`/edit-quiz/${quiz.id}`);
  };

  const handleDeleteQuiz = (quizId) => {
    setQuizzes(quizzes.filter(quiz => quiz.id !== quizId));
  };

  return (
    <div>
      <h2>Quiz Management</h2>
      {quizzes.map(quiz => (
        <QuizItem
          key={quiz.id}
          quiz={quiz}
          onEditQuiz={handleEditQuiz}
          onDeleteQuiz={handleDeleteQuiz}
        />
      ))}

      <div className="form-container">
        <button onClick={() => navigate('/edit-quiz/new')}>Create New Quiz</button>
      </div>
    </div>
  );
};

export default QuizManagement;
