import React, { useState, useEffect } from 'react';
import '../../styles/ChangePassword.css';

const ChangePassword = () => {
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [email, setEmail] = useState('');

  useEffect(() => {
    // Retrieve the email from local storage
    const storedEmail = localStorage.getItem('userEmail'); // Adjust key as needed
    if (storedEmail) {
      setEmail(storedEmail);
    }
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Validate password confirmation
    if (newPassword !== confirmPassword) {
      setError("New password and confirm password don't match.");
      return;
    }

    // Clear previous messages
    setError('');
    setSuccessMessage('');

    // Prepare the request payload
    const requestData = {
      eventID: "1001",
      addInfo: {
        email, // Use email from local storage
        currentPassword,
        newPassword,
      },
    };

    try {
      // Make the API call to change the password
      const response = await fetch('http://localhost:5164/changePassword', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData),
      });

      const data = await response.json();

      // Handle response
      if (data.rStatus === 0) {
        setSuccessMessage(data.rData.rMessage);
      } else {
        alert(data.rData.rMessage);
      }

      // Clear form fields
      setEmail('');
      setCurrentPassword('');
      setNewPassword('');
      setConfirmPassword('');
    } catch (err) {
      alert("An error occurred while changing the password.");
      console.error(err);
    }
  };

  return (
    <div>
      <h2>Change Password</h2>
      {error && <div className="error-message">{error}</div>}
      {successMessage && <div className="success-message">{successMessage}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email Id:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Current Password:</label>
          <input
            type="password"
            value={currentPassword}
            onChange={(e) => setCurrentPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>New Password:</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Confirm New Password:</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Change Password</button>
      </form>
    </div>
  );
};

export default ChangePassword;
