import React, { useState, useEffect } from 'react';
import '../../styles/userManagement.css';

const UserManagement = () => {
  const [users, setUsers] = useState([]);

  // Mock data fetching function
  useEffect(() => {
    const fetchUsers = async () => {
      const mockUsers = [
        { id: 1, name: 'User One', username: 'user1', email: 'user1@example.com', address: '123 Street', picture: 'url1' },
        { id: 2, name: 'User Two', username: 'user2', email: 'user2@example.com', address: '456 Avenue', picture: 'url2' },
        { id: 3, name: 'User Three', username: 'user3', email: 'user3@example.com', address: '789 Boulevard', picture: 'url3' },
      ];
      setUsers(mockUsers);
    };

    fetchUsers();
  }, []);

  const handleDeleteUser = (userId) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this user?");
    if (confirmDelete) {
      // Replace this with actual API call to delete user
      setUsers(users.filter(user => user.id !== userId));
    }
  };

  return (
    <div>
      <h2>User Management</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Username</th>
            <th>Email</th>
            <th>Address</th>
            <th>Picture</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.address}</td>
              <td>
                <img src={user.picture} alt={`${user.username}'s profile`} width="50" />
              </td>
              <td>
                <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserManagement;
