import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../../styles/styles.css';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      eventID: "1001",
      addInfo: {
        email: email,
        password: password
      }
    };

    try {
      const response = await axios.post('http://localhost:5164/myLogin', payload);

      console.log('API Response:', response); // Log the entire response for debugging
      let res = response.data.rData.rMessage;
      if (res === "Signin Successful") {
        const token = response.data.rData.TOKEN;
        localStorage.setItem('currentUser', JSON.stringify({ token }));
        navigate('/dashboard');
      } else {
        alert(response.data.rData.rMessage);
        // Clear form inputs on incorrect login
        setEmail('');
        setPassword('');
      }
    } catch (error) {
      console.error('Error logging in:', error);
      alert('An error occurred. Please try again.');
    }
  };

  const currentUser = JSON.parse(localStorage.serItem('currentUser'));

  return (
    <form onSubmit={handleSubmit} className="form">
      <div className="form-group">
        <label htmlFor="email">Email</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
      </div>
      <button type="submit">Login</button>
    </form>
  );
};

export default LoginForm;
