import React, { useEffect, useState } from 'react';
import '../../styles/contactusFormDetails.css'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const ContactUsFormDetails = () => {

    const [users, setUsers] = useState([]);


  const usenavigate = useNavigate() ;
//   useEffect(()=>{
//     let emailId = sessionStorage.getItem("EMAILID");
//     if(emailId==='' || emailId === null)
//       usenavigate('/login');
//   }, []);

useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.post('http://localhost:5164/viewContactUs', { eventID: "1002" });
      if (response.status === 200) {
        const responseData = response.data;
        if (responseData.rData && responseData.rData.users) {
          setUsers(responseData.rData.users);
          console.log("Users:", responseData.rData.users);
        } else {
          console.log("No users data in response");
        }
      }
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };




//   const handleDelete = async (id) => {
//     try {
//       const response = await axios.post(DELETE_API, {
//         eventID: "1002",
//         addInfo: {
//           id:id
//         }
//       });
//       console.log("Delete Response:", response.data); // Log the entire response
//       if (response.status === 200) {
//         const responseData = response.data;
//         if (responseData.rData.rMessage === "DELETE SUCCESSFULLY.") {
//           setUsers(users.filter(user => user.id !== id)); // Remove user from local state
//           console.log(`User with ID ${id} deleted successfully`);
//         } else {
//           console.log("Failed to delete user");
//         }
//       }
//     } catch (error) {
//       console.error("Error deleting user:", error);
//     }
//   };
    


  const [confirmDelete, setConfirmDelete] = useState(null);

  const handleDelete = (index) => {
    setConfirmDelete(index); // Store the index of the item to delete
  };

  const handleConfirmDelete = () => {
    onDelete(confirmDelete); // Callback to parent component to delete item
    setConfirmDelete(null); // Reset confirmation state
  };

  const handleCancelDelete = () => {
    setConfirmDelete(null); // Reset confirmation state
  };

  return (
    <div className="contact-us-table">
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Message</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map((contact, index) => (
            <tr key={index}>
              <td>{contact.name}</td>
              <td>{contact.email}</td>
              <td>{contact.message}</td>
              <td>
                {confirmDelete === index ? (
                  <div>
                    <button className="delete-button" onClick={handleConfirmDelete}>
                      Confirm Delete
                    </button>
                    <button className="delete-button" onClick={handleCancelDelete}>
                      Cancel
                    </button>
                  </div>
                ) : (
                  <button className="delete-button" onClick={() => handleDelete(index)}>
                    Delete
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ContactUsFormDetails;
